b2303443-8d09-4e94-8109-8b606bd1fd1f	c09f5076-dcb5-4af9-a03f-fa8ecfc3278a	("(""(""""(""""""""(widget)"""""""",widget)"""",id)"",8e381bcf-28cb-40b1-9867-7dee5a04a6e5)","(""(""""(widget)"""",widget)"",post_js)")	var w = $("#"+id);\n\nvar type_enum = {\n    simple: 'simple',\n    column_id: 'column_id',\n    relation_id: 'relation_id',\n    type_id: 'type_id'\n};\n\n\n\n// for checking object equality for meta id composite types\nfunction id_object_equality(obj1, obj2, type) {\n    if (type == type_enum['simple']) {\n        return obj1 == obj2;\n    }\n    else if (type == type_enum['column_id']) {\n        \n        return (\n            obj1.name == obj2.name &&\n            obj1.relation_id.name == obj2.relation_id.name &&\n            obj1.relation_id.schema_id.name == obj2.relation_id.schema_id.name\n            );\n        \n    }\n    else if (type == type_enum['relation_id']) {\n        \n        return (\n            obj1.name == obj2.name &&\n            obj1.schema_id.name == obj2.schema_id.name\n        );\n        \n    }\n    else {\n        console.error('Semantics Manager: Unsupported id comparison');\n        return;\n    }\n}\n\n\n// get a row from a rowset(arr) if field in row is equal to val\nfunction get_rowset_row(arr, field, val, type) {\n    if (arr.length < 1) {\n        console.log('Semantics Manager: Array empty');\n        return false;\n    }\n    for (var i = 0; i < arr.length; ++i) {\n        \n        console.log('Semantics Manager: Checking ', arr[i].get(field));\n        \n        if (id_object_equality(arr[i].get(field), val, type) == true) {\n        //if (arr[i].get(field) == val) {\n            console.log('Semantics Manager: Found row');\n            return arr[i];\n        }\n    }\n    // if row does not exist\n    console.log('Semantics Manager: Could not find row');\n    return false;\n}\n\n\n\n// load all tables and functions necessary for semantics lookup\nfunction load_em() {\n    var column = endpoint.schema('semantics').table('column').rows();\n    var relation = endpoint.schema('semantics').table('relation').rows();\n    var type = endpoint.schema('semantics').table('type').rows();\n    var meta_column = endpoint.schema('meta').view('column').rows();\n    var widget = endpoint.schema('widget').view('widget_name').rows();\n\n    \n    w.data('get_column_field', function() {\n        console.log ('col NOT LOADED YET.  This is a problem');\n    });\n\n    w.data('get_relation_field', function() {\n        console.log ('rel NOT LOADED YET.  This is a problem');\n    });\n\n\n    $.when(column, relation, type, meta_column, widget).then(function(columns, relations, types, meta_columns, widgets) {\n        // console.log(columns);\n        // console.log(relations);\n        \n        w.data('get_column_field', function(id, field) {\n\n            var is_widget = field.indexOf('widget_id') != -1;\n\n            // find the semantics.column row if it exists\n            var col = get_rowset_row(columns, 'id', id, type_enum['column_id']);\n            if (col != false && col.get(field) != null) {\n\n                if (!is_widget) {\n                    // return field\n                    return col.get(field);\n                }\n                else {\n                    // return widget name\n                    var widget = get_rowset_row(widgets, 'id', col.get(field), type_enum['simple']);\n                    \n                    if (widget != null) {\n                        return widget.name;\n                    }\n                    else {\n                        return false;\n                    }\n                }\n\n\n            }\n\n            // find the meta.column row and retrieve type_id\n            var meta_col = get_rowset_row(meta_columns, 'id', id, type_enum['column_id']);\n            if (meta_col != false) {\n                var type_id = meta_col.get('type_id');\n                if (type_id != false) {\n\n                    // find the semantics.type row for this type_id\n                    var type = get_rowset_row(types, 'type_id', type_id, type_enum['type_id']);\n                    if (type != false) {\n\n                        if (!is_widget) {\n                            // return field\n                            return type.get(field);\n                        }\n                        else {\n                            // return widget name\n                            var widget = get_rowset_row(widgets, 'id', type.get(field), type_enum['simple']);\n\n                            if (widget != null) {\n                                return widget.get('name');\n                            }\n                            else {\n                                return false;\n                            }\n                        }\n                    }\n                }\n            }\n\n            // should never be reached if input is sane\n            // emtpy(null) fields will also evaluate to null, so this will be indistinguishable to empty field\n            return null;\n\n        });\n\n        w.data('get_relation_field', function(id, field) {\n            \n            console.log('Looking for semantics.relation row', id);\n\n            var relation = get_rowset_row(relations, 'id', id, type_enum['relation_id']);\n            if (relation != false) {\n\n                var is_widget = field.indexOf('widget_id') != -1;\n\n                if (!is_widget) {\n                    console.log('Field is not a widget_id');\n                    return relation.get(field);\n                }\n                else {\n                    console.log('Widget field!');\n                    // return widget name\n                    var widget = get_rowset_row(widgets, 'id', relation.get(field), type_enum['simple']);\n\n                    if (widget != null) {\n                        return widget.get('name');\n                    }\n                    else {\n                        return false;\n                    }\n                }\n            }\n            else {\n                console.log('Relation does not have row in semantics');\n                return null;\n            }\n\n            // should never be reached if input is sane\n            // emtpy(null) fields will also evaluate to null, so this will be indistinguishable to empty field\n            return null;\n\n        });\n\n        //console.log('Semantics loaded: ', relations, columns, types, meta_columns, widgets);\n        console.log('Semantics loaded');\n\n    });\n\n}\n\nload_em();\n\n\nw.find('input.show').click(function() {\n    alert ('not done');\n});\n\n\nw.find('button.reload').click(function() {\n    load_em();\n});\n\n\n
