var w = $("#"+id);

var remotes = {
    label: 'Remotes',
    content: function() {
        return widget('admin:table_edit', {
            rowset: endpoint.schema('bundle').table('remote_database').rows({ limit: 20 }),
            item_name: function(row) {
                return row.get('foreign_server_name');
            }
        })
    }
}

var manager = {
    label: 'Manager',
    content: function() {
        return widget('b:remotes', {} );
    }
}

w.find('.content').html(widget('layout:tabs', {
    tabs: [
        { label: manager.label,  content: manager.content },
        { label: remotes.label,  content: remotes.content }
    ],
    selected_tab_color: '#ccc'
}));

var w = $("#"+id);

var bundle_diff = endpoint.schema('bundle').function('remote_diff', [
    'bundle',
    remote.get('schema_name')
]);

widget.sync(bundle_diff, w.find('.diff'), function(diff_row) {
    return widget('b:remote_summary_diff_row', {
        diff_row: diff_row,
        remote_schema_name: remote.get('schema_name')
    });
});

var w = $("#"+id);

// remote has, local doesn't
if (diff_row.get('b1_id') == null) {
    w.find('.remote').show();
    w.find('.remote').append(diff_row.get('b2_name'));
    w.find('button.clone').show();
}
// local has, remote doesn't
else {
    w.find('.local').show();
    w.find('.local').append(diff_row.get('b1_name'));
    w.find('button.push').show();
}


w.find('button.clone').click(function() {
    console.log(diff_row);

    endpoint.schema('bundle').function('remote_clone', [
		diff_row.get('b2_id'),
        remote_schema_name,
        'bundle'
    ]).then(function() {
        alert('ok');
    }).catch(function(e,o) {
        alert('error');
        console.log('ERROR',e,o);
    });
});



w.find('button.push').click(function() {
    console.log(diff_row);

    endpoint.schema('bundle').function('remote_clone', [
		diff_row.get('b1_id'),
        'bundle',
        remote_schema_name
    ]).then(function() {
        alert('ok');
    }).catch(function(e,o) {
        alert('error');
        console.log('ERROR',e,o);
    });
});

